package operators;

public class ArithmeticExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 11;
        int b = 5;

        System.out.println("a + b = " + (a + b)); // Addition: 15
        System.out.println("a - b = " + (a - b)); // Subtraction: 5
        System.out.println("a * b = " + (a * b)); // Multiplication: 50
        System.out.println("a / b = " + (a / b)); // Division: 2, dividend
        System.out.println("a % b = " + (a % b)); // Modulus: 0, remainder

	}

}
